document.addEventListener('DOMContentLoaded',function(){if(typeof AOS!=='undefined'){AOS.init({duration:800,easing:'ease-in-out',once:true,offset:100,mirror:true})}window.toggleMenu=function(){const a=document.getElementById("myNavbar");if(a){if(a.classList.contains("responsive")){a.classList.remove("responsive")}else{a.classList.add("responsive")}}};const b=document.querySelectorAll('.navbar a:not(.icon)');const c=document.getElementById("myNavbar");if(c&&b.length>0){b.forEach(d=>{d.addEventListener('click',()=>{if(window.innerWidth<=768){c.classList.remove("responsive")}})})}document.addEventListener('click',(e)=>{const f=document.getElementById("myNavbar");if(f&&!e.target.closest('.navbar')){f.classList.remove('responsive')}});const g=document.querySelector('.loading-screen');if(g){window.addEventListener('load',()=>{g.style.opacity='0';setTimeout(()=>{g.style.display='none'},500)})}const h=document.getElementById('backToTop');if(h){window.addEventListener('scroll',()=>{if(window.pageYOffset>300){h.style.display='flex';h.style.opacity='1'}else{h.style.opacity='0';setTimeout(()=>{if(window.pageYOffset<=300){h.style.display='none'}},300)}});h.addEventListener('click',()=>{window.scrollTo({top:0,behavior:'smooth'})})}const i=document.querySelectorAll('.service');i.forEach(j=>{j.addEventListener('mouseenter',()=>{j.style.transform='translateY(-5px)';const k=j.querySelector('i');if(k){k.style.transform='scale(1.2)'}});j.addEventListener('mouseleave',()=>{j.style.transform='translateY(0)';const k=j.querySelector('i');if(k){k.style.transform='scale(1)'}})});document.querySelectorAll('a[href^="#"]').forEach(l=>{l.addEventListener('click',function(e){const m=this.getAttribute('href');if(m==='#'||m.length<2){e.preventDefault();return}const n=document.querySelector(m);if(n){e.preventDefault();n.scrollIntoView({behavior:'smooth'})}})});const o=document.querySelectorAll('.request-btn, .message-btn, .more-btn, .availability, .pricing');o.forEach(p=>{p.addEventListener('mouseenter',()=>{p.style.transform='translateY(-3px)';p.style.boxShadow='0 6px 20px rgba(0, 0, 0, 0.3)'});p.addEventListener('mouseleave',()=>{p.style.transform='translateY(0)';p.style.boxShadow='0 4px 15px rgba(0, 0, 0, 0.2)'})});const q=document.querySelector('.bg');if(q){window.addEventListener('scroll',()=>{const r=window.pageYOffset;q.style.backgroundPositionY=-(r*0.5)+'px'})}const s=document.querySelectorAll('.dropdown');s.forEach(t=>{t.addEventListener('mouseenter',()=>{const u=t.querySelector('.dropdown-content');if(u){u.style.display='block';u.style.opacity='1';u.style.transform='translateY(0)'}});t.addEventListener('mouseleave',()=>{const u=t.querySelector('.dropdown-content');if(u){u.style.opacity='0';u.style.transform='translateY(-10px)';setTimeout(()=>{u.style.display='none'},300)}})});const v=window.matchMedia('(max-width: 768px)');function handleTabletChange(e){const w=document.querySelector('.actions');if(w){w.style.flexDirection=e.matches?'column':'row'}}v.addListener(handleTabletChange);handleTabletChange(v);const x=document.querySelectorAll('[data-aos]');x.forEach(y=>{y.style.opacity='0';y.style.transform='translateY(20px)';const z=new IntersectionObserver((aa)=>{aa.forEach(bb=>{if(bb.isIntersecting){y.style.opacity='1';y.style.transform='translateY(0)';y.style.transition='all 1000ms cubic-bezier(0.5, 0, 0, 1) 200ms';z.unobserve(bb.target)}})});z.observe(y)});const cc=document.querySelector('.profile-img');if(cc){cc.addEventListener('mouseenter',()=>{cc.style.transform='scale(1.05) rotate(5deg)'});cc.addEventListener('mouseleave',()=>{cc.style.transform='scale(1) rotate(0)'})}});window.myFunction=function(dd){const ee=document.getElementById("imgModal");const ff=document.getElementById("expandedImg");const gg=document.getElementById("imgtext");if(ee&&ff&&gg){ee.style.display="flex";ff.src=dd.src;gg.innerHTML=dd.alt;setTimeout(()=>{ff.classList.add('modal-zoom-in')},10)}};window.closeModal=function(){const hh=document.getElementById("imgModal");const ii=document.getElementById("expandedImg");if(hh&&ii){ii.classList.remove('modal-zoom-in');ii.classList.add('modal-zoom-out');setTimeout(()=>{hh.style.display="none";ii.classList.remove('modal-zoom-out')},300)}};window.addEventListener('click',(jj)=>{const kk=document.getElementById("imgModal");if(kk&&jj.target===kk){window.closeModal()}});